import { useEffect, useState } from "react"
import { getContent } from "../Service/getContent"
import style from "../css/Grid.module.css"
import { Card } from "./Card";

export function Grid() {

  const [content,setContent] = useState([]);
  
  const urlDiscovered = "/discover/movie?api_key=3&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false";

  useEffect(()=>{
    const response = getContent(urlDiscovered).then((response) => {
    let data = response.data;
    setContent(data.results)
    }); 
  },[]);
  
 
   
  
 
  return (
   <div className={style.Grid}>
    { content.map((movie)=>{
      return <Card key={movie.id} content={movie}/>
    })}
   </div>
  )

 
}
