import style from "../css/Card.module.css"
export  function Card({content}) {

  const urlImage =  "https://image.tmdb.org/t/p/w300" + content.poster_path;
  return (
    <div className={style.Card}>
      <img className={style.Image} src={urlImage} alt={content.Title}></img>
      <p className={style.Title}>{content.Title}</p>
    </div>
  )
}
