import { useEffect, useState } from "react"
import { getContent } from "../Service/getContent"
import style from "../css/Grid.module.css"
import { Card } from "./Card";
import { LoadingIcon } from "../components/LoadingIcon"
import { useQuery } from "../hooks/useQuery"
import { URLContentSelector } from "../Service/URLContentSelector"
import InfiniteScroll from 'react-infinite-scroll-component';




export function Grid() {

  const [films,setFilms] = useState([]);
  
  const [isLoading,setIsLoading]=useState(false);

  const query = useQuery().get("search");

  const [page,setPage] = useState(1);

  const[hasMore, setHasMore]= useState(true);


  console.log(page)
  useEffect(()=>{
    setIsLoading(true);
      getContent(URLContentSelector(query)).then((res) => {
      let data = res.data;
      setFilms(data.results)
      setHasMore(data.total_pages>data.page)
      setIsLoading(false);
      }); 
  },[query,page]);


  // if(isLoading===true){
  //   return(
  //     <LoadingIcon className={style.LoadingIcon}/>)

  //   }else{
      
  //   return (
  //     <div className={style.Grid}>
  //      { films.map((film,key)=>{
  //        return <Card key={key} film={film}/>
  //      })}
  //     </div>
  //    )
  // }


  return(<InfiniteScroll
  dataLength={films.length}
  next={() => setPage((prevPage) => prevPage + 1)}
  hasMore={hasMore}
  loader={<isLoading/>}>
            <div className={style.Grid}>
              {films.map
                ((film,key)=>
                  {
                    return <Card key={key} film={film}/>
                  }
                )
              }
            </div>      
  </InfiniteScroll>)
      
