import { useEffect, useState } from "react"
import { getContent } from "../Service/getContent"
import style from "../css/Grid.module.css"
import { Card } from "./Card";
import { LoadingIcon } from "../components/LoadingIcon"
import { useQuery } from "../hooks/useQuery"
import { URLManager } from "../Service/URLManager";


export function Grid() {

  const [films,setFilms] = useState([]);
  
  const urlDiscovered = "/discover/movie?api_key=3&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false";
  const urlSearch = "/search/movide/"

  const [isLoading,setIsLoading]=useState(false);

  const query = useQuery().get("search");
  
  


  useEffect(()=>{
    setIsLoading(true);
    getContent(URLManager(query)).then((res) => {
    let data = res.data;
    setFilms(data.results)
    
    setIsLoading(false);
    }); 
  },[query]);

  if(isLoading===true){
    return(
      <LoadingIcon className={style.LoadingIcon}/>)

    }else{
    return (
      <div className={style.Grid}>
       { films.map((film)=>{
         return <Card key={film.id} film={film}/>
       })}
      </div>
     )
  }
}
