import { useEffect, useState } from "react"
import { getContent } from "../Service/getContent"
import style from "../css/Grid.module.css"
import { Card } from "./Card";
import { LoadingIcon } from "../components/LoadingIcon"
// import { useQuery } from "../hooks/useQuery"
import { URLContentSelector } from "../Service/URLContentSelector"
import InfiniteScroll from 'react-infinite-scroll-component';


export function Grid({ search }) {

  const [films,setFilms] = useState([]);
  
  const [isLoading,setIsLoading]=useState(false);

  // const [page,setPage] = useState(1);

  const [initialized, setInitialized] = useState(false);

  const[hasMore, setHasMore]= useState(true);



  // useEffect(()=>{
  //   setIsLoading(true);
  //     getContent(URLContentSelector(search,page)).then((res) => {
  //     let data = res.data;
  //     setFilms((prev) => prev.concat(data.results))
  //     setHasMore(data.page < data.total_pages)
  //     setIsLoading(false);
  //     }); 
  // },[search,page]);
  let page = 1;
  console.log(page)

  const getNewFilms=()=>{
    page=page+1;
    setIsLoading(true);
    getContent(URLContentSelector(search,page)).then((res) => 
    {
      let data = res.data;
      setFilms(films.concat(data.results))
      setHasMore(data.page < data.total_pages)
      setInitialized(true)
      setIsLoading(false);
     });
  }
         
        

      
  useEffect(() => {

    if(initialized===false){
      getNewFilms();  
    }
             
  },[]);


  return(
  <InfiniteScroll
  dataLength={films.length}
  // next={() => setPage((prevPage) => prevPage + 1)}
  next={getNewFilms}
  hasMore={hasMore}
  loader={<LoadingIcon/>}>
            <div className={style.Grid}>
              {films.map
                ((film,key)=>
                  {
                    return <Card key={key} film={film}/>
                  }
                )
              }
            </div>      
  </InfiniteScroll>
  )
      
}
